function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
	   }
      //Type appropriate comment here, and begin script below
	try{
		if (g_form.getValue('u_requested_for') =='62826bf03710200044e0bfc8bcbe5df1'){
			confirm(g_form.getReference('u_requested_for') + "?");
			var caller = g_form.getReference('u_requested_for', doAlert); // doAlert is our callback function
		} else {
			alert("reference = " + g_form.getReference('caller_id')
			+ ", \n u_requested_for = " + g_form.getValue('u_requested_for')
				  + ", \n g_user.firstName = " + g_user.firstName
				  + ", \n g_user.lastName = " + g_user.lastName
				  + ", \n g_user.userName = " + g_user.userName
				  + ", \n g_user.userID = " + g_user.userID);
			jslog("ME: u_requested_for = " + g_form.getValue('u_requested_for'));  
		}
	} 	
	catch(err){
		// write info in jslog
		jslog("ME: err.name = " + err.name);   
		jslog("ME: err.message = " + err.message);   
		jslog("ME: g_user.firstName = " + g_user.firstName);   
		jslog("ME: g_user.lastName = " + g_user.lastName); 
		jslog("ME: The NeedIt State value = " + g_form.getValue('state'));
		jslog("ME: The currently logged in user is " + g_user.getFullName() + ".");

		// write info to browser console (Both messages appear in the browser debugger)
		jslog("This message was written by the jslog() method.");
		console.log("This message was written by console.log().");
	}

	function doAlert(caller) { //reference is passed into callback as first arguments
				g_form.setValue('description',g_form.getReference('u_requested_for') + " -  Updated field value");
	}
}
